import { useEffect, useState } from "react"
import { useLocation } from "react-router-dom"
const EditPage=()=>{
    const location= useLocation()
    const [itemNo,setItemNo]= useState(0)
    const [description,setDescription]= useState('')
    const [rate,setRate]= useState(0)
    const [qty,setQty]= useState(0)
    const [amount,setAmount]= useState(0)
    let itemNoOld= location.state.itemNo

    const item:{itemNo:number, unit:string, description:string, rate:number, qty:number,amount:number}=
        {
            itemNo:1.1,
            description:"Clearing of Ordinary Soil, Root and a made up Ground of the site to an average depth of 20 cm from natural ground level",
            unit:"m2",	 
            rate:840.00,
            qty:18.00,
            amount: 15120.00
        }
        const handleClick=()=>{
            console.log(amount,rate)
        }
        useEffect(()=>{
            setItemNo(itemNoOld)
            setDescription(location.state.description)
            setRate(location.state.rate)
            setQty(location.state.qty)
            setAmount(location.state.amount)
            console.log(itemNoOld)

        },[])
    return(
        <div>
            <form>
                <p>Item No</p>
                <input type="number" value={itemNoOld}/>
                <p> Description</p>
                <input type="text" defaultValue={location.state.description} onChange={(e)=>setDescription(e.target.value)}/>
                <th>Rate</th>
                <input type="number" defaultValue={location.state.rate} onChange={(e)=>setRate(e.target.valueAsNumber)}/>
                <th>Qty</th>
                <input  type="number" defaultValue={location.state.qty} onChange={(e)=>setQty(e.target.valueAsNumber)}/>
                <th>Amount</th>
                <input  type="number" defaultValue={location.state.amount} onChange={(e)=>setAmount(e.target.valueAsNumber)}/>
                <button onClick={handleClick}>Edit</button>
            </form>
        </div>
    )

}
export default EditPage
