import React, { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"


const TablePage=()=>{
    const [items,setItems]= useState("")
    useEffect(()=>{
        fetch("/").then(
            response=>response.json()
        )
        .then(
            data=>{
                setItems(data),
                console.log(data)
            }
        )

},[])
    const navigate= useNavigate()
    const data:{itemNo:number, unit:string, description:string, rate:number, qty:number,amount:number}[]=[
        {
            itemNo:1.1,
            description:"Clearing of Ordinary Soil, Root and a made up Ground of the site to an average depth of 20 cm from natural ground level",
            unit:"m2",	 
            rate:840.00,
            qty:18.00,
            amount: 15120.00
        },
        {
            itemNo:1.2,
            description:"Bulk excavation in an ordinary soil  to a depth not exceeding 150 cm from  Cleared Level",
            unit:"m3",	 
            rate:1247.09,
            qty:122.00 ,
            amount: 15120.00
        }


    ]

    const handleEdit=(itemNo:number)=>{
        console.log(itemNo)
        navigate("/edit",{
            state:{
                itemNo:itemNo
            }
        })

    }

    const handleDelete=(itemNo:number)=>{
        console.log(itemNo)
        navigate("/delete",{
            state:{
                itemNo:itemNo
            }
        })
    }
    
    return(
        <div>
            <table>
                <tr>
                    <th>Item No</th>
                    <th> Description</th>
                    <th>Rate</th>
                    <th>Qty</th>
                    <th>Amount</th>
                </tr>
                
                    {data.map(item=>(
                        <tr key={item.itemNo}>
                            <td>{item.itemNo}</td>
                            <td>{item.description}</td>
                            <td>{item.rate}</td>
                            <td>{item.qty}</td>
                            <td>{item.amount}</td>
                            <tr onClick={()=>handleEdit(item.itemNo)}>Edit</tr>
                            <tr onClick={()=>handleDelete(item.itemNo)}>Remove</tr>
                        </tr>

                    ))}


                
               


            </table>
        </div>
    )

}
export default TablePage
