import { useEffect, useState } from "react"
import { useLocation } from "react-router-dom"
const EditPage=()=>{
    const location= useLocation()
    const [itemNo,setItemNo]= useState(0)
    const [description,setDescription]= useState('')
    const [rate,setRate]= useState(0)
    const [qty,setQty]= useState(0)
    const [amount,setAmount]= useState(0)
    let itemNoOld= location.state.itemNo
    const item:{itemNo:number, unit:string, description:string, rate:number, qty:number,amount:number}=
        {
            itemNo:1.1,
            description:"Clearing of Ordinary Soil, Root and a made up Ground of the site to an average depth of 20 cm from natural ground level",
            unit:"m2",	 
            rate:840.00,
            qty:18.00,
            amount: 15120.00
        }
        const handleClick=()=>{
            console.log(amount,rate)
        }
        useEffect(()=>{
            setItemNo(itemNoOld)
            setDescription(item.description)
            setRate(item.rate)
            setQty(item.qty)
            setAmount(item.amount)
            console.log(itemNoOld)

        },[])
    return(
        <div>
            <p>Item No</p>
            <input type="number" value={itemNoOld}/>
            <p> Description</p>
            <input type="text" defaultValue={item.description} onChange={(e)=>setDescription(e.target.value)}/>
            <th>Rate</th>
            <input type="number" defaultValue={item.rate} onChange={(e)=>setRate(e.target.valueAsNumber)}/>
            <th>Qty</th>
            <input  type="number" defaultValue={item.qty} onChange={(e)=>setQty(e.target.valueAsNumber)}/>
            <th>Amount</th>
            <input  type="number" defaultValue={item.amount} onChange={(e)=>setAmount(e.target.valueAsNumber)}/>
            <button onClick={handleClick}>Edit</button>
        </div>
    )

}
export default EditPage
