import { useEffect, useState } from "react"
import { useLocation } from "react-router"
import { useNavigate } from "react-router-dom"
const DeletePage=()=>{
    const location= useLocation()

    const navigate= useNavigate()
    const [description,setDescription]= useState('')
    const itemNo= location.state.itemNo

    useEffect(()=>{
        setDescription(location.state.description)
    },[])

    const handleClick=()=>{
        const formData= {itemNo,description}
            fetch('http://localhost:8000/delete',{
                method:'DELETE',
                headers:{'Content-Type':'application/json'},
                body:JSON.stringify(formData)
            }).then(response=>response.json())
            .then((data)=>console.log(data))
            .then(()=>
                navigate('/')
            )
            .catch((error)=>console.error(error))
    }
    
    return(
        <div>
            <p>Item No</p>
            <input type="number" value={location.state.itemNo}/>
            <p> Description</p>
            <input type="text" value={location.state.description} />
            <th>Rate</th>
            <input type="number" value={location.state.rate} />
            <th>Qty</th>
            <input  type="number" value={location.state.qty} />
            <th>Amount</th>
            <input  type="number" value={location.state.amount}/>
            <p>Are you sure you want to delete?</p>
            <button onClick={handleClick}>Yes</button>
            <button onClick={()=>navigate('/')}>Go Back</button>
        </div>
    )
}
export default DeletePage
