import { useEffect, useState } from "react"
import { useLocation } from "react-router-dom"
const EditPage=()=>{
    const location= useLocation()
    const [amount,setAmount]= useState(0)
    let itemNo= location.state.itemNo
    console.log(itemNo)
    const item:{itemNo:number, unit:string, description:string, rate:number, qty:number,amount:number}=
        {
            itemNo:1.1,
            description:"Clearing of Ordinary Soil, Root and a made up Ground of the site to an average depth of 20 cm from natural ground level",
            unit:"m2",	 
            rate:840.00,
            qty:18.00,
            amount: 15120.00
        }
        useEffect(() => {
          setAmount(item.itemNo)
    
        }, [])
        
        
    return(
        <div>
            <p>Item No</p>
            <input type="number" defaultValue={amount} />
            <p> Description</p>
            <input type="text" value={item.description}/>
            <th>Rate</th>
            <input type="number" value={item.rate}/>
            <th>Qty</th>
            <input  type="number" value={item.qty}/>
            <th>Amount</th>
            <input  type="number" value={amount} onChange={(e)=>setAmount(e.target.valueAsNumber)}/>
        </div>
    )

}
export default EditPage
